public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 11; //initializing the 1st value
		int y = 3; // initializing the 2nd value
		//starting if statement
		if(x+y < 10) { 
			System.out.println(x + " + " + y + " is less than 10"); // printing result
		}
		// starting else statement 
		else {
			System.out.println(x + " + " + y + " is not less than 10"); //printing result 
		}
	}

	}