public class IfElseLadder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "Riya";
		if (name == "Meena") {
			System.out.println("The name is Meena");
		} 
		else if (name == "Neha") {
			System.out.println("The name is Neha");
		}
		else {
			System.out.println(name);
		}
	}

}
